var bikky=document.cookie;function getCookie(B){var A=bikky.indexOf(B+"=");if(A==-1){return null}A=bikky.indexOf("=",A)+1;var C=bikky.indexOf(";",A);if(C==-1){C=bikky.length}return unescape(bikky.substring(A,C))}var today=new Date();var expiry=new Date(today.getTime()+28*24*60*60*1000);function setCookie(A,B){if(B!=null&&B!=""){document.cookie=A+"="+escape(B)+"; expires="+expiry.toGMTString()}bikky=document.cookie}function toggle(id){var element=document.getElementById(id);with(element.style){if(display=="none"){display="";setCookie(id,"show")}else{display="none";setCookie(id,"hide")}}var text=document.getElementById(id+"-switch").firstChild;if(text.nodeValue=="[show >>]"){text.nodeValue="[hide]"}else{text.nodeValue="[show >>]"}}function cookieHide(C){if(getCookie(C)=="hide"){var A=document.getElementById(C);A.style.display="none";var B=document.getElementById(C+"-switch").firstChild;B.nodeValue="[show >>]"}};